import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useRecoilState } from 'recoil';
import { accessTokenState } from '../hooks/Auth';
import '../styles/LoginPage.css';
import Nav  from '../components/nav.jsx'
import Footer from '../components/footer.jsx'

const LoginPage = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [accessToken, setAccessToken] = useRecoilState(accessTokenState);
  const navigate = useNavigate();

  useEffect(() => {
    console.log(accessToken);
  }, [accessToken]);

  const handleLogin = async (event) => {
    event.preventDefault();
    setLoading(true);
    setError('');

    try {
      console.log(process.env.REACT_APP_API_URL)
      const response = await fetch(`${process.env.REACT_APP_API_URL}/user/auth/login/`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          username,
          password,
        }),
      });

      const data = await response.json();
      console.log(data);
      if (response.ok) {
        console.log('Login successful:', data.access);
        setAccessToken(data.access);
        console.log('Access token set in Recoil:', accessToken); // 디버깅용 콘솔 로그
        navigate(-2);
      } else {
        throw new Error(data.message || 'Failed to login');
      }
    } catch (error) {
      console.error('Login error:', error);
      setError(error.message || 'An unexpected error occurred.');
    }

    setLoading(false);
  };

  return (
    <div>
      <Nav></Nav>
    <div className="login-container">
      <h2>Login</h2>
      <form onSubmit={handleLogin} className="login-form">
        <div className="input-group">
          <label htmlFor="username">Username:</label>
          <input
            type="text"
            id="username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
        </div>
        <div className="input-group">
          <label htmlFor="password">Password:</label>
          <input
            type="password"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>
        <button type="submit" disabled={loading}>
          {loading ? 'Logging in...' : 'Login'}
        </button>
        {error && <div className="error-message">{error}</div>}
      </form>
    </div>
    <Footer></Footer>
    </div>
  );
};

export default LoginPage;
