import React, { useEffect, useState } from 'react';
import { useParams, useLocation } from 'react-router-dom';
import { useNavigate } from 'react-router-dom'; // useNavigate 추가

const LevelDetailPage = () => {
  const { id } = useParams();  // URL로부터 id 추출
  const [levelDetail, setLevelDetail] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const navigate = useNavigate(); // useNavigate 훅 사용

  useEffect(() => {
    const fetchLevelDetail = async () => {
      try {
        const response = await fetch(`${process.env.REACT_APP_API_URL}/api/questions/${id}`);
        const data = await response.json();
        if (response.ok) {
          console.log(data);
          setLevelDetail(data);
        } else {
          throw new Error(data.message || 'Something went wrong');
        }
      } catch (err) {
        setError(err.message);
        console.error('Error fetching level detail:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchLevelDetail();
  }, [id]);  // 의존성 배열에 id를 포함하여 id가 변경될 때마다 데이터를 다시 불러옴

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;

  const getStarRating = (accuracy) => {
    if (accuracy < 25) return 0;
    if (accuracy < 50) return 1;
    if (accuracy < 75) return 2;
    return 3;
  };

  const goToLevelDetail = (id) => {
    navigate(`/level/${id}`, { state : id}); // 이동할 URL 설정
  };

  const renderStars = (count) => {
    return (
      <div className="stars">
        {[...Array(3)].map((_, i) => (
          <span key={i} className={i < count ? 'star filled' : 'star'}>★</span>
        ))}
      </div>
    );
  };

  return (
    <div>
      <h1>Level Details</h1>
      <div className="container">
      {levelDetail.map((level) => (
            <div key={level.id} className="level-item">
            <h2>Level {level.question_number}: {level.word}</h2>
            <button onClick={() => goToLevelDetail(level.id)}>Go to Level {level.id} Details</button>
            {renderStars(getStarRating(level.incorrect_pronounciation.length))}
            </div>
        ))}
      </div>
      {levelDetail ? (
        <div>
          <h2>Level {levelDetail.id}: {levelDetail.name}</h2>
          <p>Accuracy: {levelDetail.accuracy}%</p>
          {renderStars(getStarRating(levelDetail.accuracy))}
        </div>
      ) : (
        <p>No details available for this level.</p>
      )}
    </div>
  );
};

export default LevelDetailPage;
